        /** Display Handler for display state of the component **/
        [Bindable]
        public function get displayHandler():IUxDisplayHandler {
            return _displayHandler;
        }

        /** Display Handler for display state of the component **/
        public function set displayHandler(handler:IUxDisplayHandler):void {
            var length:int = _displayHandlerWatchers.length;
            for (var i:int = 0; i < length; i++) {
                (_displayHandlerWatchers.pop() as ChangeWatcher).unwatch();
            }
            _displayHandler = handler;
            if (_displayHandler != null) {
                _displayHandlerWatchers.push(BindingUtils.bindProperty(this,
                    "enabled", _displayHandler, "enabled"));
                _displayHandlerWatchers.push(BindingUtils.bindProperty(this,
                    "visible", _displayHandler, "visible"));
                _displayHandlerWatchers.push(BindingUtils.bindProperty(this,
                    "includeInLayout", _displayHandler, "visible"));
            }
        }

        /**
         * Automation name settings.
         * override function to add prefix and suffix to internal automationName.
         */
        override public function set automationName(value:String):void {
            if (value != null && value.length > 0) {
                _internalAN = value;
            }
            super.automationName =
                UxAutomationUtils.getFormattedAutomationName(this.className,
                value, "_BTN");
        }

