        ux_internal function get parentAN():String {
            return _parentAN;
        }

        /**
         * Automation name settings.
         * Internal function to set parent's automationName.
         */
        ux_internal function set parentAN(value:String):void {
            _parentAN = value;
        }

        /**
         * Automation name settings.
         * override function to add parent's automation name to internal automationName.
         */
        override public function get automationName():String {
            var value:String = _internalAN;
            if (_parentAN != null) {
                value = _parentAN + "_" + _internalAN;
            }
            return UxAutomationUtils.getFormattedAutomationName(this.className,
                value, "_BTN");

