private function showErrorMessage():void {
	var title:String = 
		UxResourceManager.getInstance().getString(RESOURCE_BUNDLE, "JAVASERVICE_ERROR_TITLE");
	var message:String =
		UxResourceManager.getInstance().getString(RESOURCE_BUNDLE,
			"JAVASERVICE_ERROR_MESSAGE");
	var dlg:ECUITPopupDialog =
		ECUITPopupDialog.showMessageDialog(this, message, title,
			ECUITPopupDialog.ERROR_MESSAGE);
	dlg.addEventListener(ECUITPopupDialogEvent.POPUP_DIALOG_RESULT,
		handleJavaError);
}

