        /** Update to turn off effect if in HLM*/
        override public function play():void {
            // obtain current duration
            actualDuration = this.duration;
            var isHLM:Boolean =
                UxModeService.isModeActive(UxModeService.HIGH_LATENCY_MODE_KEY);

            // if HLM than set duration to 0
            if (isHLM) {
                this.duration = 0;
                super.play();
            } else {
                super.play();
            }
            // set the proper duration back incase the user changes HLM back.
            this.duration = actualDuration;

        private function onToolsMenuUpdate(message:UxViewBlockToolsMenuMessage):void {

            if (message.viewblock == this) {
                if (message.category == UxViewBlockToolsMenuMessage.
                    UPDATE_TOOLS_MENU) {
                    var menuProvider:Function = message.payload as Function;
                    if (message.toolsHandlerComponent) {
                        _menuProviderMap[UIDUtil.getUID(message.
                            toolsHandlerComponent)] = menuProvider;
                    } else {
                        _menuProviderMap[DEFAULT_MENU_ID] = menuProvider;

            this.addEventListener(FlexEvent.ADD, panelShown);
        }

